let STEP;
let graphics = [];
let sands = [];
let colors = [
  '#0D1E40','#224573','#5679A6','#F2A25C','#D96B43',
  '#323140','#7B7DA6','#BABCD9','#BF8339','#BFA18F',
]
let sand;

function setup() {
  frameRate(120)
  createCanvas(windowWidth, windowHeight);
  colorMode(HSB)
  background(10)
  STEP = 10 * PI * 0.0125;

  for (let i = 0; i < 30; i++) {
    graphics[i] = new Spiral()
  }

  for (let i = 0; i < 30; i++) {
    sands[i] = new Spiral(true)
  }
  clear()
}

function draw() {
  clear()

  if (frameCount > 500) {
    noLoop()
  }

  for (let graphic of graphics) {
    graphic.draw()
  }
 for (let sand of sands) {
    sand.draw()
  }
}

class Spiral {
  constructor(sand) {
    this.graphics = createGraphics(width, height)
    this.r = 10000
    this.rot = radians(random(0, 360))
    this.color = random(colors)
    this.theta = 0
    this.i = 0
    this.sand = sand
  }

  draw () {
    this.graphics.push();
    this.graphics.fill(this.color);
    this.graphics.stroke(this.color);
    this.graphics.strokeWeight(0.03)
    this.graphics.translate(width/2, height/2)
    this.graphics.rotate(this.rot)
    this.theta += STEP * random(1, 3);
    let rad1 = this.theta;
    let rad2 = this.theta + STEP;
    let x = this.r / rad1 * cos(this.theta);
    let y = this.r / rad1 * sin(this.theta);
    let plus = 1

    if (!this.sand) {
      if ((this.i + 1) % 5 == 0) {
        strokeWeight(1)
        plus = 25
        this.graphics.line(x, y, x + plus, y + plus)
      }
      else if ((this.i + 1) % 14 == 0) {
        this.graphics.square(x, y, 5)
      }
      else if ((this.i + 1) % 32 == 0) {
        this.graphics.triangle(x, y, x + 10, y, x, y + 10)
      }
      else {
        this.graphics.line(x, y, x + plus, y + plus)
      }

    } else {
      this.r = 30000
      strokeWeight(10)
      this.graphics.line(x, y, x + 1, y + 1)
    }

    image(this.graphics, 0, 0);
    this.graphics.pop();
    this.i += 1
  }
}

